import Team from "./Team";
import { MyContext } from "./App";
import { useContext, useEffect, useState } from "react";


function TeamList({ }) {
    const {teams, setTeams} = useContext(MyContext)
    const {login, setLogin} = useContext(MyContext)
    const {user, setUser} = useContext(MyContext)
    const {coaches} = useContext(MyContext)
    const [listTeams, setListTeams] = useState([])

    useEffect(() => {
        coaches.map((coach) => {
            if (coach.id === user.id) {
                setListTeams([coach.teams])
            }
        })
    }, [])


    console.log(listTeams)
    const allTeams = teams.map((team) => {
        return(
            <Team
                key={team.id}
                city={team.city}
                mascot={team.mascot}
                wins={team.wins}
                theme={team.theme}
                rosters={team.rosters}
            />
        )
    })



    return(
        <div className="teampage">
            <h1 className="teamtitle">Teams</h1>
            <ul className="teamlist">
                {login ? (
                    <>
                    {listTeams.map((team) => (
                        <Team
                        key={team.id}
                        city={team.city}
                        mascot={team.mascot}
                        wins={team.wins}
                        theme={team.theme}
                        rosters={team.rosters}
                        />
                    ))}
                    </>
                ):(
                    [allTeams]
                )}
            </ul>
        </div>
    )
}

export default TeamList;
