import Team from "./Team";
import { MyContext } from "./App";
import { useContext, useEffect, useState } from "react";


function TeamList({ }) {
    const {teams, setTeams} = useContext(MyContext)
    const {login, setLogin} = useContext(MyContext)
    const {user, setUser} = useContext(MyContext)
    const {coaches} = useContext(MyContext)

    const allTeams = teams.map((team) => {
        return(
            <>
                <Team
                key={team.id}
                city={team.city}
                mascot={team.mascot}
                wins={team.wins}
                theme={team.theme}
                rosters={team.rosters}
                />
            </>
        )
    })

    const coachTeams = teams.map((team) => {
        const coach = team.coaches.map((coach) => {
