        return(
            <Team
                key={team.id}
                city={team.city}
                mascot={team.mascot}
                wins={team.wins}
                theme={team.theme}
                rosters={team.rosters}
            />
        )
    }
    })
    })

    return(
        <div className="teampage">
            <h1 className="teamtitle">Teams</h1>
            {login ? (
                <ul className="teamlist">
                    {coachTeams}
                </ul>
            ):(
                <ul className="teamlist">
                    {allTeams}
                </ul>
            )}

        </div>
    )
}

export default TeamList;
