import Team from "./Team";
import { MyContext } from "./App";
import { useContext, useEffect, useState } from "react";


function TeamList({ }) {
    const {teams, setTeams} = useContext(MyContext)
    const {login, setLogin} = useContext(MyContext)
    const {user, setUser} = useContext(MyContext)
    const {coaches} = useContext(MyContext)

    const allTeams = teams.map((team) => {
        return(
            <Team
                key={team.id}
                city={team.city}
                mascot={team.mascot}
                wins={team.wins}
                theme={team.theme}
                rosters={team.rosters}
            />
        )
    })

    const listTeams = coaches.map((coach) => {
        if (coach.id === user.id) {
            coach.teams.map((team) => {
                return(
                    <Team
                    key={team.id}
                    city={team.city}
                    mascot={team.mascot}
                    wins={team.wins}
                    theme={team.theme}
                    />
                )
            })
        }
    })

    console.log(listTeams)
    return(
        <div className="teampage">
            <h1 className="teamtitle">Teams</h1>
            {login ? (
                <ul className="teamlist">
                    {listTeams}
                </ul>
            ):(
                <ul className="teamlist">
                    {allTeams}
                </ul>
            )}

        </div>
    )
}

export default TeamList;
